div.script:
// onchange main selector server config
function onchange_email_server_config(elem){
    if(elem.value == 'none'){
        document.getElementById('email_server_incoming').style.display = "none";
        document.getElementById('email_server_outgoing').style.display = "none";
    } else if(elem.value == 'in'){
        document.getElementById('email_server_incoming').style.display = "";
        document.getElementById('email_server_outgoing').style.display = "none";
    } else if(elem.value == 'out'){
        document.getElementById('email_server_incoming').style.display = "none";
        document.getElementById('email_server_outgoing').style.display = "";
    } else if(elem.value == 'all'){
        document.getElementById('email_server_incoming').style.display = "";
        document.getElementById('email_server_outgoing').style.display = ""; 
        document.getElementById('email_server_outgoing').style.paddingTop = "20px"; 
    }
}

// onchange incoming server
function onchange_in_email_server_type(elem){
    // pop
    if(elem.value == 'pop'){
        if (document.getElementById('incoming_email_server_security').checked){
            document.getElementById('incoming_email_server_port').value = "995";
        } else {
            document.getElementById('incoming_email_server_port').value = "110";
        }
    }

    // imap
    if(elem.value == 'imap'){
        if (document.getElementById('incoming_email_server_security').checked){
            document.getElementById('incoming_email_server_port').value = "993";
        }else{
            document.getElementById('incoming_email_server_port').value = "143";
        }
    }
}
function onchange_in_email_server_security(elem){
    onchange_in_email_server_type(document.getElementById('incoming_email_server_type'))
}

// onchange outoging server
function onchange_outgoing_email_server_security(elem){
    if(elem.value == 'none'){
        document.getElementById('outgoing_email_server_port').value = "";
    } else if(elem.value == 'ssl'){
       document.getElementById('outgoing_email_server_port').value = "465";
    } else if(elem.value == 'starttls'){
       document.getElementById('outgoing_email_server_port').value = "25";
    }
}
